package binaryoperator;

public class AssigmentOperator {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a=10;
		 a+=5;
		 System.out.println(a);
		 a-=10;
         System.out.println(a);
         a*=10;
         System.out.println(a);
         a/=10;
         System.out.println(a);
         a%=5;
         System.out.println(a);
         
	}

}
