package string;

public class ReverseString {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
          String str="vamsi";
          System.out.println(str.length());
          
          for(int i=str.length()-1;i>=0;i--)
          {
        	  System.out.print(str.charAt(i));
          }
	}

}
